import React from 'react'
import { View, Text, ScrollView, TouchableOpacity, StyleSheet } from 'react-native'
import RadioButtonRN from 'radio-buttons-react-native';

const RadioData = [
    {
      label: 'Value 1'
     },
     {
      label: 'Value 2'
     },
     {
        label: 'Value 3'
       },
       {
        label: 'Value 4'
       }
    ];

const Reasoning = ({ navigation }) => {
    return(
        <ScrollView style={styles.mainContainer}>
            <View>
        <Text style={styles.headingDirection}>Directions</Text>
            </View>
            <View style={styles.view1}>
                <Text style={styles.directionText}>1. Attempt all questions.</Text>
                <Text style={styles.directionText}>2. Following are the mcq type questions.</Text>
                <Text style={styles.directionText}>3. Select one of the 4 options.</Text>
            </View>
            <View style={styles.bisect}></View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q1. If NOIDA is written as OPJEB, then what will be the code for DELHI?</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q2. If AIRLINE is written as ENILRIA7, then RAILWAY will be written as -</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q3. If CAT is coded as PATC, JOY is coded as POYJ; similarly the word WING will be coded as -</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q4. Which number should come next in the series 1, 2, 3, 10, __</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q5. Which number is wrong in the series 2, 6, 15, 31, 56, 93?</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q6. The number comes next in the series 12, 36, 109, 329, ... -</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q7. Suppose a series is 6, 11, 21, 36, 56, ? the number comes at the place of question mark in the given series is -</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q8. Suppose a series is 1, 9, 25, 49, ?, 121, the number comes at the place of question mark in the given series is -</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q9. The number comes after the series 8, 28, 116, 584, ......</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q10. What will be the next number in the series 13, 17, 19, 23, 29, .......?</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q11. What will be the missing number in the series 196, 169, 144, __, 100, 81?</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q12. If PINK is coded as 1691411, then RED will be coded as -</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>
            <View style={styles.bisect}></View>
            <View style={styles.view3}>
                <TouchableOpacity style={styles.submit}>
                    <Text style={styles.submitText}>Submit Answers</Text>
                </TouchableOpacity>
            </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    mainContainer:{
        flex: 1,
        marginVertical:15,
        marginHorizontal:12
    },  
    view1:{
        marginVertical:5
    },
    view2:{
        padding:7, 
        marginVertical:5
    },
    view3:{
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical:20,
        marginBottom:50,
    },
    headingDirection:{
        fontSize:25,
        fontWeight:'bold'
    },
    directionText:{
        fontSize:18
    },
    bisect:{
        borderBottomWidth:1,
        marginVertical:10
    },
    quesText:{
        fontSize:15,
        fontWeight:'bold'
    },
    submit:{
        height:40,
        justifyContent: 'center', 
        alignItems: 'center',
        borderRadius:8,
        borderWidth:1,
        borderColor:'dodgerblue',
        width:'100%',
    },
    submitText:{
        fontSize:20,
        color:'dodgerblue'
    }
})

export default Reasoning
