import React from 'react';
import { View, Text, ScrollView, TouchableOpacity, StyleSheet } from 'react-native'
import RadioButtonRN from 'radio-buttons-react-native';

const RadioData = [
    {
      label: 'Value 1'
     },
     {
      label: 'Value 2'
     },
     {
        label: 'Value 3'
       },
       {
        label: 'Value 4'
       }
    ];


const English = ({ route, navigation }) => {
    let { apiData } = route.params;
    console.log("Api data from home page :: ",apiData)
    
    let Eng_Questions = apiData.data.map((item,index) => {
        return item.question
    })

    let QuesView = Eng_Questions.filter((item,index) => {
        Eng_Questions.map((value,ind) => {
            if(value === item){
                return(
                <View key={index}>
                    <Text style={styles.quesText}>{value}</Text>
                </View>
                )
            }
        })
    })

    // let radioValues = apiData.data.map((item,index) => {
    //     return {
    //         question
    //     }
    // })

    return(
        <ScrollView style={styles.mainContainer}>
            <View>
        <Text style={styles.headingDirection}>Directions</Text>
            </View>
            <View style={styles.view1}>
                <Text style={styles.directionText}>1. Attempt all questions.</Text>
                <Text style={styles.directionText}>2. Following are the mcq type questions.</Text>
                <Text style={styles.directionText}>3. Select one of the 4 options.</Text>
            </View>
            <View style={styles.bisect}></View>
            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q1. The origin of the word communication is _______.</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>

            <View style={styles.view2}>
                <View>
                    <Text style={styles.quesText}>Q2.  Types of words used for verbal communication?</Text>
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>

            <View style={styles.view2}>
                <View>
                    {Eng_Questions}
                    <RadioButtonRN
                    data={RadioData}
                    circleSize={10}
                    animation={true}
                    animationTypes={['rotate']}
                    selectedBtn={(e) => console.log(e)}
                    />
                </View>
            </View>

            <View style={styles.bisect}></View>
            <View style={styles.view3}>
                <TouchableOpacity style={styles.submit}>
                    <Text style={styles.submitText}>Submit Answers</Text>
                </TouchableOpacity>
            </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    mainContainer:{
        flex: 1,
        marginVertical:15,
        marginHorizontal:12
    },  
    view1:{
        marginVertical:5
    },
    view2:{
        padding:7, 
        marginVertical:5
    },
    view3:{
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical:20,
        marginBottom:50,
    },
    headingDirection:{
        fontSize:25,
        fontWeight:'bold'
    },
    directionText:{
        fontSize:18
    },
    bisect:{
        borderBottomWidth:1,
        marginVertical:10
    },
    quesText:{
        fontSize:15,
        fontWeight:'bold'
    },
    submit:{
        height:40,
        justifyContent: 'center', 
        alignItems: 'center',
        borderRadius:8,
        borderWidth:1,
        borderColor:'dodgerblue',
        width:'100%',
    },
    submitText:{
        fontSize:20,
        color:'dodgerblue'
    }
})

export default English
